mapper.select:
SELECT count(*)
FROM tbl_review
WHERE tp_number = #{tp_number}

mapper.update:
UPDATE tbl_review
SET rv_content = #{rv_content}, rv_star = #{rv_star}
WHERE rv_number = #{rv_number}

mapper.select:
SELECT ROUND(AVG(rv_star), 1)
FROM tbl_review
WHERE TP_NUMBER = #{tp_number}

mapper.update:
UPDATE tbl_tutor_page
SET TP_AVERAGE = #{tp_average}
WHERE TP_NUMBER = #{tp_number}
