mapper.update:
UPDATE tbl_user
user_pw=#{user_pw}, user_nick=#{user_nick}, user_name=#{user_name}, user_gender=#{user_gender}, user_birth=#{user_birth}, user_phone=#{user_phone}, st_school=#{st_school}, st_grade=#{st_grade}, tt_school=#{tt_school}, tt_subject=#{tt_subject}, tt_depart=#{tt_depart}
WHERE user_email = #{user_email}

UPDATE  tbl_user
SET HANDS_WALLET = HANDS_WALLET + #{h_count}
WHERE user_email= #{user_email}

UPDATE  tbl_user
SET HANDS_WALLET = HANDS_WALLET - #{h_count}
WHERE user_email= #{user_email}
